#include <vector>\n#include <string>\n#include <sstream>\n#include <iostream>\n\nstd::string joinStrings(const std::vector<std::string>& strings) {\n    std::ostringstream oss;\n    for (size_t i = 0; i < strings.size(); ++i) {\n        if (i != 0) {\n            oss << " ";\n        }\n        oss << strings[i];\n    }\n    return oss.str();\n}\n\nint main() {\n    std::vector<std::string> vec = {"sample", "string", "for", "this", "example"};\n    std::string result = joinStrings(vec);\n    std::cout << result << std::endl; // Output: "sample string for this example"\n    return 0;\n}