#include <windows.h>\n#include <iostream>\n\nbool IsFileWritable(const char* filePath) {\n    HANDLE hFile = CreateFile(\n        filePath,\n        GENERIC_WRITE,\n        0,\n        NULL,\n        OPEN_EXISTING,\n        FILE_ATTRIBUTE_NORMAL,\n        NULL\n    );\n\n    if (hFile == INVALID_HANDLE_VALUE) {\n        return false;\n    }\n    \n    CloseHandle(hFile);\n    return true;\n}\n\nvoid WaitForFileUnlock(const char* filePath) {\n    while (!IsFileWritable(filePath)) {\n        Sleep(1000); // Wait for 1 second before retrying\n    }\n    std::cout << "File is now writable: " << filePath << std::endl;\n}\n\nint main() {\n    const char* filePath = "C:\\path\\to\\your\\file.txt";\n    WaitForFileUnlock(filePath);\n    // Perform your desired operations here\n    return 0;\n}