// Example usage\nGtk::TreeView treeView;\nint headerHeight = get_treeview_header_height(treeView);\nint numberRowsToDisplay = 10;\nint heightOfRow = 20; // Change this according to your row height\n\n// Set the height of the container (a scrolled window)\nGtk::ScrolledWindow scrolledWindow;\nscrolledWindow.add(treeView);\nscrolledWindow.set_size_request(-1, headerHeight + numberRowsToDisplay * heightOfRow);