#include <QColor>\n\nQColor getTextColor(const QColor &background) {\n    double luminance = (0.299 * background.red() + 0.587 * background.green() + 0.114 * background.blue()) / 255.0;\n    return luminance > 0.5 ? QColor(0, 0, 0) : QColor(255, 255, 255);\n}\n\n// Example usage:\nQColor backgroundColor(100, 150, 200);  // RGB values\nQColor textColor = getTextColor(backgroundColor);\nqDebug() << "Text color should be:" << textColor;