#include <windows.h>\n\n// Create and select a font that supports Unicode\nHFONT hFont = CreateFont(\n    20,                    // Height of font\n    0,                     // Width of font\n    0,                     // Angle of escapement\n    0,                     // Orientation angle\n    FW_NORMAL,             // Font weight\n    FALSE,                 // Italic attribute option\n    FALSE,                 // Underline attribute option\n    FALSE,                 // Strikeout attribute option\n    DEFAULT_CHARSET,       // Character set identifier\n    OUT_DEFAULT_PRECIS,    // Output precision\n    CLIP_DEFAULT_PRECIS,   // Clipping precision\n    DEFAULT_QUALITY,       // Output quality\n    DEFAULT_PITCH | FF_DONTCARE, // Pitch and family\n    L"Arial Unicode MS");  // Font name\n\n// Select the font into the device context\nHDC hdc = GetDC(hwnd);  // hwnd is the handle to your window\nSelectObject(hdc, hFont);\n\n// Now you can draw text with Unicode characters\nTextOut(hdc, x, y, L"Your Unicode Text", wcslen(L"Your Unicode Text"));\n\n// Clean up\nDeleteObject(hFont);\nReleaseDC(hwnd, hdc);