#include <iostream>\n#include <list>\n#include <memory>\n\nclass Node {\npublic:\n    int data;\n    Node(int val) : data(val) {}\n};\n\nclass MasterSlave {\npublic:\n    void push_front(std::shared_ptr<Node> node) {\n        slave_list.push_front(node);\n    }\n\nprivate:\n    std::list<std::shared_ptr<Node>> slave_list;\n};\n\nint main() {\n    MasterSlave master;\n    std::shared_ptr<Node> node = std::make_shared<Node>(10);\n\n    try {\n        master.push_front(node);\n        std::cout << "Node added successfully." << std::endl;\n    } catch (const std::exception &e) {\n        std::cerr << "Exception: " << e.what() << std::endl;\n    }\n\n    return 0;\n}