#import "YourComLibrary.tlb" raw_interfaces_only\n\nusing namespace YourComLibrary;\n\nint main()\n{\n    CoInitialize(nullptr);\n\n    IYourComInterfacePtr pYourComInterface;\n    HRESULT hr = pYourComInterface.CreateInstance(__uuidof(YourComClass));\n    if (SUCCEEDED(hr))\n    {\n        try\n        {\n            pYourComInterface->YourMethod();\n        }\n        catch (const _com_error& e)\n        {\n            std::wcerr << L"COM error: " << e.ErrorMessage() << std::endl;\n            std::wcerr << L"Description: " << e.Description() << std::endl;\n        }\n    }\n    else\n    {\n        std::cerr << "Failed to create COM instance" << std::endl;\n    }\n\n    CoUninitialize();\n    return 0;\n}