#include <iostream>\n#include <fstream>\n#include <string>\n\nint main() {\n    std::ifstream file("example.txt");\n    if (!file.is_open()) {\n        std::cerr << "Failed to open the file." << std::endl;\n        return 1;\n    }\n\n    std::string line;\n    while (std::getline(file, line)) {\n        std::cout << line << std::endl;\n    }\n\n    file.close();\n    return 0;\n}