#include <windows.h>\n#include <iostream>\n#include <vector>\n\nvoid DrawTextToConsole(HANDLE hConsole, const std::string& text, COORD pos) {\n    DWORD charsWritten;\n    WriteConsoleOutputCharacterA(hConsole, text.c_str(), text.length(), pos, &charsWritten);\n}\n\nint main() {\n    HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);\n    if (hConsole == INVALID_HANDLE_VALUE) {\n        std::cerr << "Error getting console handle" << std::endl;\n        return 1;\n    }\n\n    COORD pos = {0, 0};\n    std::string text = "Hello, World!";\n    \n    // Draw text at the specified position\n    DrawTextToConsole(hConsole, text, pos);\n\n    // Example: Update console at 10 Hz\n    const int updateRateHz = 10;\n    const int updateIntervalMs = 1000 / updateRateHz;\n    \n    std::vector<std::string> lines = {\n        "Line 1: Serial output",\n        "Line 2: More serial data",\n        "Line 3: Even more data"\n    };\n\n    while (true) {\n        for (size_t i = 0; i < lines.size(); ++i) {\n            COORD linePos = {0, static_cast<SHORT>(i)};\n            DrawTextToConsole(hConsole, lines[i], linePos);\n        }\n        Sleep(updateIntervalMs);\n    }\n\n    return 0;\n}