#include <vector>\n#include <iostream>\n#include <algorithm>\n\nusing namespace std;\n\nconst int kLastNumber = 11; // last number expected in array\nconst int kFirstNumber = 3; // first number expected in array\n\nint main()\n{\n    vector<int> myVector = {4, 5, 5, 5, 7, 8, 9, 10, 10};\n\n    // Put the results in here\n    vector<int> missingValues;\n    vector<int> duplicates;\n\n    // Initialize iterators\n    auto it = myVector.begin();\n    int expected = kFirstNumber;\n\n    while (expected <= kLastNumber) {\n        if (it != myVector.end() && *it == expected) {\n            // Check for duplicates\n            if (it + 1 != myVector.end() && *(it + 1) == expected) {\n                duplicates.push_back(expected);\n                ++it;\n            }\n            ++it;\n        } else {\n            // Missing value\n            missingValues.push_back(expected);\n        }\n        ++expected;\n    }\n\n    // Display missing values\n    cout << "Missing Values: ";\n    for (const auto& val : missingValues) {\n        cout << val << " ";\n    }\n    cout << endl;\n\n    // Display duplicates\n    cout << "Duplicate Values: ";\n    for (const auto& val : duplicates) {\n        cout << val << " ";\n    }\n    cout << endl;\n\n    return 0;\n}