#include <iostream>\n#include <hpdf.h>\n\nvoid error_handler(HPDF_STATUS error_no, HPDF_STATUS detail_no, void *user_data) {\n    std::cerr << "ERROR: error_no=" << error_no << ", detail_no=" << detail_no << std::endl;\n}\n\nint main() {\n    HPDF_Doc pdf = HPDF_New(error_handler, nullptr);\n    if (!pdf) {\n        std::cerr << "ERROR: Cannot create PDF object." << std::endl;\n        return 1;\n    }\n\n    HPDF_Page page = HPDF_AddPage(pdf);\n    HPDF_Page_SetSize(page, HPDF_PAGE_SIZE_A4, HPDF_PAGE_PORTRAIT);\n\n    HPDF_Page_BeginText(page);\n    HPDF_Page_SetFontAndSize(page, HPDF_GetFont(pdf, "Helvetica", nullptr), 24);\n    HPDF_Page_TextOut(page, 50, 750, "Hello, PDF!");\n    HPDF_Page_EndText(page);\n\n    if (HPDF_SaveToFile(pdf, "example.pdf") != HPDF_OK) {\n        std::cerr << "ERROR: Cannot save PDF file." << std::endl;\n        HPDF_Free(pdf);\n        return 1;\n    }\n\n    HPDF_Free(pdf);\n    std::cout << "PDF created successfully." << std::endl;\n\n    return 0;\n}