#include <iostream>\n#include <functional>\n\n// Sample function to be bound\nvoid sampleFunction(int a, int b) {\n    std::cout << "a: " << a << ", b: " << b << std::endl;\n}\n\nint main() {\n    // Binding sampleFunction with placeholders\n    auto boundFunction = std::bind(sampleFunction, std::placeholders::_1, 2);\n\n    // Calling the bound function with one argument\n    boundFunction(1);\n\n    return 0;\n}