#include <iostream>\n#include <cstring>\n#include <uuid/uuid.h>\n\nvoid bytesToGUID(const char* bytes, uuid_t& guid) {\n    memcpy(guid, bytes, 16);\n}\n\nvoid guidToBytes(const uuid_t& guid, char* bytes) {\n    memcpy(bytes, guid, 16);\n}\n\nint main() {\n    // Simulate received byte array from Java\n    char receivedBytes[16];\n\n    // Fill receivedBytes with data (example)\n    for (int i = 0; i < 16; ++i) {\n        receivedBytes[i] = static_cast<char>(i);\n    }\n\n    uuid_t guid;\n    bytesToGUID(receivedBytes, guid);\n\n    // Print the GUID in string format\n    char guidString[37];\n    uuid_unparse(guid, guidString);\n    std::cout << "Received GUID: " << guidString << std::endl;\n\n    // Convert back to bytes and print\n    char bytes[16];\n    guidToBytes(guid, bytes);\n    std::cout << "Bytes: ";\n    for (int i = 0; i < 16; ++i) {\n        std::cout << std::hex << (0xFF & static_cast<int>(bytes[i])) << " ";\n    }\n    std::cout << std::endl;\n\n    return 0;\n}