#include <windows.h>\n#include <objbase.h>\n\nint main()\n{\n    CoInitialize(NULL);\n\n    CLSID clsid;\n    HRESULT hr = CLSIDFromProgID(L"YourProgID", &clsid);\n\n    if (SUCCEEDED(hr))\n    {\n        IUnknown* pUnknown = NULL;\n        hr = CoCreateInstance(clsid, NULL, CLSCTX_LOCAL_SERVER, IID_IUnknown, (void**)&pUnknown);\n\n        if (SUCCEEDED(hr))\n        {\n            // Handle the COM object as needed\n            pUnknown->Release();\n        }\n    }\n\n    CoUninitialize();\n    return 0;\n}