#include <list>\n\nclass MyClass {\npublic:\n    MyClass() {}\n\n    // Define casting operators\n    operator std::list<unsigned char>() const { \n        std::list<unsigned char> a; \n        return a; \n    }\n\n    operator unsigned char() const { \n        unsigned char a; \n        return a; \n    }\n};\n\nint main() {\n    MyClass a;\n    // Use static_cast to explicitly specify the desired conversion\n    std::list<unsigned char> b = static_cast<std::list<unsigned char>>(a);\n\n    return 0;\n}