#include <curl/curl.h>\n   #pragma comment(lib, "libcurl.lib")\n\n   bool FTPGetFile::ConnectToFTP(std::string ftpServer) {\n       CURL *curl;\n       CURLcode res;\n       curl = curl_easy_init();\n       if (curl) {\n           curl_easy_setopt(curl, CURLOPT_URL, ftpServer.c_str());\n           res = curl_easy_perform(curl);\n           curl_easy_cleanup(curl);\n           return (res == CURLE_OK);\n       } else {\n           return false;\n       }\n   }