#include <iostream>\n#include <fstream>\n#include <stdexcept>\n\nclass MyClass {\npublic:\n    MyClass() = default;\n\n    void initialize(const std::string& filename) {\n        std::ifstream file(filename);\n        if (!file.is_open()) {\n            throw std::runtime_error("File not found");\n        }\n        // Read the file...\n    }\n};\n\nMyClass staticObject; // Static object declaration\n\nint main() {\n    try {\n        staticObject.initialize("input.txt");\n    } catch (const std::exception& ex) {\n        std::cerr << "Error: " << ex.what() << std::endl;\n    }\n\n    return 0;\n}