#include <windows.h>\n#include <mmdeviceapi.h>\n#include <endpointvolume.h>\n#include <iostream>\n\nint main() {\n    HRESULT hr;\n    CoInitialize(NULL);\n\n    IMMDeviceEnumerator *pEnumerator = NULL;\n    hr = CoCreateInstance(__uuidof(MMDeviceEnumerator), NULL, CLSCTX_INPROC_SERVER, __uuidof(IMMDeviceEnumerator), (void**)&pEnumerator);\n\n    if (FAILED(hr)) {\n        std::cerr << "Failed to create device enumerator: " << hr << std::endl;\n        return -1;\n    }\n\n    IMMDevice *pDevice = NULL;\n    hr = pEnumerator->GetDefaultAudioEndpoint(eCapture, eConsole, &pDevice);\n\n    if (FAILED(hr)) {\n        if (hr == E_NOTFOUND) {\n            std::cout << "No default recording device found." << std::endl;\n        } else {\n            std::cerr << "Failed to get default audio endpoint: " << hr << std::endl;\n        }\n    } else {\n        std::cout << "Default recording device is present." << std::endl;\n        pDevice->Release();\n    }\n\n    pEnumerator->Release();\n    CoUninitialize();\n    return 0;\n}