#include <podofo/podofo.h>\n#include <iostream>\n\nusing namespace PoDoFo;\n\nint main() {\n    PdfStreamedDocument outputPdf("output.pdf");\n\n    // Load the first PDF file\n    PdfMemDocument pdf1;\n    try {\n        pdf1.Load("input1.pdf");\n    } catch (const PdfError& e) {\n        std::cerr << "Error loading input1.pdf: " << e.what() << std::endl;\n        return -1;\n    }\n\n    // Append pages from the first PDF\n    for (int i = 0; i < pdf1.GetPageCount(); ++i) {\n        outputPdf.InsertPages(pdf1, i, 1);\n    }\n\n    // Load the second PDF file\n    PdfMemDocument pdf2;\n    try {\n        pdf2.Load("input2.pdf");\n    } catch (const PdfError& e) {\n        std::cerr << "Error loading input2.pdf: " << e.what() << std::endl;\n        return -1;\n    }\n\n    // Append pages from the second PDF\n    for (int i = 0; i < pdf2.GetPageCount(); ++i) {\n        outputPdf.InsertPages(pdf2, i, 1);\n    }\n\n    // Save the output PDF\n    outputPdf.Close();\n\n    std::cout << "PDF files combined successfully!" << std::endl;\n\n    return 0;\n}