#include <gtkmm.h>\n\nint get_treeview_header_height(Gtk::TreeView& treeView) {\n    // Ensure headers are visible to measure them\n    treeView.set_headers_visible(true);\n\n    // Get the header window\n    Glib::RefPtr<Gdk::Window> headerWindow = treeView.get_header_window();\n    \n    int headerHeight = 0;\n    if (headerWindow) {\n        Gdk::Rectangle headerRect;\n        headerWindow->get_visible_region().get_clipbox(headerRect);\n        headerHeight = headerRect.get_height();\n    }\n\n    return headerHeight;\n}