// Example: Simple Stack Implementation in C++\n   #include <iostream>\n   #include <vector>\n   \n   class Stack {\n   private:\n       std::vector<int> stack;\n   \n   public:\n       void push(int value) {\n           stack.push_back(value);\n       }\n   \n       void pop() {\n           if (!stack.empty()) {\n               stack.pop_back();\n           } else {\n               std::cout << "Stack is empty!" << std::endl;\n           }\n       }\n   \n       int top() {\n           if (!stack.empty()) {\n               return stack.back();\n           } else {\n               std::cout << "Stack is empty!" << std::endl;\n               return -1; // Error value\n           }\n       }\n   \n       bool isEmpty() {\n           return stack.empty();\n       }\n   };\n   \n   int main() {\n       Stack s;\n       s.push(10);\n       s.push(20);\n       std::cout << "Top element is " << s.top() << std::endl; // Output: 20\n       s.pop();\n       std::cout << "Top element is " << s.top() << std::endl; // Output: 10\n       return 0;\n   }