#include <windows.h>\n#include <iostream>\n\nbool create_large_file(const char* filename, LARGE_INTEGER size) {\n    HANDLE hFile = CreateFile(\n        filename,\n        GENERIC_WRITE,\n        0,\n        NULL,\n        CREATE_ALWAYS,\n        FILE_ATTRIBUTE_NORMAL,\n        NULL\n    );\n\n    if (hFile == INVALID_HANDLE_VALUE) {\n        std::cerr << "Failed to create file. Error: " << GetLastError() << std::endl;\n        return false;\n    }\n\n    if (!SetFilePointerEx(hFile, size, NULL, FILE_BEGIN)) {\n        std::cerr << "Failed to set file pointer. Error: " << GetLastError() << std::endl;\n        CloseHandle(hFile);\n        return false;\n    }\n\n    if (!SetEndOfFile(hFile)) {\n        std::cerr << "Failed to set end of file. Error: " << GetLastError() << std::endl;\n        CloseHandle(hFile);\n        return false;\n    }\n\n    CloseHandle(hFile);\n    return true;\n}\n\nint main() {\n    LARGE_INTEGER size;\n    size.QuadPart = 8LL * 1024 * 1024 * 1024; // 8 GB\n\n    if (create_large_file("largefile.bin", size)) {\n        std::cout << "File created successfully." << std::endl;\n    } else {\n        std::cerr << "File creation failed." << std::endl;\n    }\n\n    return 0;\n}