#include <iostream>\n\nint main() {\n    int rows = 3;\n    int cols = 4;\n\n    // Allocate memory for the array of pointers (rows)\n    int** array = new int*[rows];\n\n    // Allocate memory for each row (columns)\n    for (int i = 0; i < rows; ++i) {\n        array[i] = new int[cols];\n    }\n\n    // Example: Initialize the array with values\n    for (int i = 0; i < rows; ++i) {\n        for (int j = 0; j < cols; ++j) {\n            array[i][j] = i * cols + j;\n        }\n    }\n\n    // Example: Print the array\n    for (int i = 0; i < rows; ++i) {\n        for (int j = 0; j < cols; ++j) {\n            std::cout << array[i][j] << ' ';\n        }\n        std::cout << std::endl;\n    }\n\n    // Deallocate memory\n    for (int i = 0; i < rows; ++i) {\n        delete[] array[i]; // Deallocate each row\n    }\n    delete[] array; // Deallocate the array of pointers\n\n    return 0;\n}