#include <iostream>\n#include <vector>\n#include <algorithm>\n\nint main() {\n    std::vector<int> vec = {1, 2, 3, 4, 5};\n\n    // Using the std::for_each algorithm with iterators\n    std::for_each(vec.begin(), vec.end(), [](int &n) {\n        n *= 2;\n    });\n\n    // Output the modified vector\n    for (const auto &n : vec) {\n        std::cout << n << " ";\n    }\n\n    return 0;\n}