#include <windows.h>\n#include <shlobj.h>\n#include <tchar.h>\n\nvoid SaveUserInformation(const std::string& data) {\n    // Define the path buffer and get the Application Data folder path\n    TCHAR path[MAX_PATH];\n    if (SUCCEEDED(SHGetFolderPath(NULL, CSIDL_APPDATA, NULL, 0, path))) {\n        // Append your application-specific subfolder and file name\n        std::string filePath = std::string(path) + "\\YourAppName\\user_info.dat";\n\n        // Ensure the directory exists\n        CreateDirectory(std::string(path + std::string("\\YourAppName")).c_str(), NULL);\n\n        // Save encrypted data to the file\n        std::ofstream outFile(filePath, std::ios::binary);\n        if (outFile.is_open()) {\n            outFile.write(data.data(), data.size());\n            outFile.close();\n        }\n    }\n}