// main.cpp\n   #include <windows.h>\n   #include <iostream>\n\n   typedef void(*HelloFunc)();\n\n   int main() {\n       HINSTANCE hinstLib = LoadLibrary(TEXT("mylibrary.dll"));\n       if (hinstLib != NULL) {\n           HelloFunc hello = (HelloFunc)GetProcAddress(hinstLib, "hello");\n           if (hello != NULL) {\n               hello();\n           } else {\n               std::cerr << "Could not locate the function." << std::endl;\n           }\n           FreeLibrary(hinstLib);\n       } else {\n           std::cerr << "Could not load the DLL." << std::endl;\n       }\n       return 0;\n   }