#include <sstream>\n#include <string>\n\nstd::string formatString(int value, const std::string& text) {\n    std::ostringstream oss;\n    oss << "Value: " << value << ", Text: " << text;\n    return oss.str();\n}\n\nint main() {\n    int value = 42;\n    std::string text = "Hello World";\n    std::string result = formatString(value, text);\n    std::cout << result << std::endl;\n    return 0;\n}