#include <boost/assert.hpp>\n\n#define BOOST_ENABLE_ASSERT_HANDLER\n\nvoid boost_assertion_failed(char const * expr,\n                            char const * function,\n                            char const * file,\n                            long line) {\n    std::cerr << "Assertion failed: " << expr << " in " << function\n              << " at " << file << ":" << line << std::endl;\n    std::terminate();\n}\n\n#define CUSTOM_ASSERT(condition, message) \\n    if (!(condition)) { \\n        boost::assertion_failed(#condition, __FUNCTION__, __FILE__, __LINE__); \\n    }\n\nint main() {\n    int num = -1;\n    CUSTOM_ASSERT(num > 0, "The number must be greater than zero!");\n    return 0;\n}