#include <e32base.h>\n#include <coemain.h>\n#include <coecntrl.h>\n#include <eikapp.h>\n#include <eikdoc.h>\n#include <eikstart.h>\n\nclass CExampleAppUi : public CEikAppUi\n{\npublic:\n    void ConstructL();\n    ~CExampleAppUi();\nprivate:\n    void HandleCommandL(TInt aCommand);\n};\n\nvoid CExampleAppUi::ConstructL()\n{\n    BaseConstructL();\n}\n\nCExampleAppUi::~CExampleAppUi()\n{\n}\n\nvoid CExampleAppUi::HandleCommandL(TInt aCommand)\n{\n    switch (aCommand)\n    {\n    case EEikCmdExit:\n        Exit();\n        break;\n    default:\n        break;\n    }\n}\n\nclass CExampleDocument : public CEikDocument\n{\npublic:\n    static CExampleDocument* NewL(CEikApplication& aApp);\nprivate:\n    CExampleDocument(CEikApplication& aApp);\n    CEikAppUi* CreateAppUiL();\n};\n\nCExampleDocument* CExampleDocument::NewL(CEikApplication& aApp)\n{\n    CExampleDocument* self = new (ELeave) CExampleDocument(aApp);\n    return self;\n}\n\nCExampleDocument::CExampleDocument(CEikApplication& aApp) : CEikDocument(aApp)\n{\n}\n\nCEikAppUi* CExampleDocument::CreateAppUiL()\n{\n    return new (ELeave) CExampleAppUi;\n}\n\nclass CExampleApplication : public CEikApplication\n{\nprivate:\n    CApaDocument* CreateDocumentL();\n    TUid AppDllUid() const;\n};\n\nCApaDocument* CExampleApplication::CreateDocumentL()\n{\n    return CExampleDocument::NewL(*this);\n}\n\nTUid CExampleApplication::AppDllUid() const\n{\n    return TUid::Uid(0xE80000E3); // Change to your UID3\n}\n\nLOCAL_C CApaApplication* NewApplication()\n{\n    return new CExampleApplication;\n}\n\nGLDEF_C TInt E32Main()\n{\n    return EikStart::RunApplication(NewApplication);\n}