#include <iostream>\n#include <sqlite3.h>\n\nint main() {\n    sqlite3* db;\n    sqlite3_stmt* stmt;\n    const char* sql = "SELECT * FROM users WHERE id = ?";\n\n    // Open database\n    if (sqlite3_open("example.db", &db)) {\n        std::cerr << "Can't open database: " << sqlite3_errmsg(db) << std::endl;\n        return 1;\n    }\n\n    // Prepare the SQL statement\n    if (sqlite3_prepare_v2(db, sql, -1, &stmt, nullptr) != SQLITE_OK) {\n        std::cerr << "Failed to prepare statement: " << sqlite3_errmsg(db) << std::endl;\n        return 1;\n    }\n\n    // Bind the parameter value\n    int userId = 1;\n    if (sqlite3_bind_int(stmt, 1, userId) != SQLITE_OK) {\n        std::cerr << "Failed to bind parameter: " << sqlite3_errmsg(db) << std::endl;\n        return 1;\n    }\n\n    // Execute the query\n    while (sqlite3_step(stmt) == SQLITE_ROW) {\n        // Process each row of the result set\n        std::cout << "User ID: " << sqlite3_column_int(stmt, 0) << std::endl;\n        std::cout << "Username: " << sqlite3_column_text(stmt, 1) << std::endl;\n    }\n\n    // Finalize the statement\n    sqlite3_finalize(stmt);\n\n    // Close the database\n    sqlite3_close(db);\n\n    return 0;\n}