#include <cstdio>\n#include <cstring>\n#include <string>\n#include <unistd.h>  // For fileno and _chsize\n\nint main() {\n    FILE *file = fopen("yourfile.txt", "r+");\n    if (file == nullptr) {\n        perror("Failed to open file");\n        return 1;\n    }\n\n    // Read the file contents\n    std::string str;\n    char cLine[256];  // Buffer to hold each line\n\n    while (fgets(cLine, sizeof(cLine), file)) {\n        str.append(cLine);\n    }\n\n    // Rewind the file pointer to the beginning for writing\n    rewind(file);\n\n    // Write the string back to the file\n    fputs(str.c_str(), file);\n\n    // Truncate the file to the size of the string\n    _chsize(fileno(file), (int)str.size());\n\n    // Close the file\n    fclose(file);\n\n    return 0;\n}