#include <windows.h>\n#include <iostream>\n\nbool ReadRegistryValue(HKEY hKeyRoot, LPCSTR subKey, LPCSTR valueName, LPBYTE data, DWORD dataSize) {\n    HKEY hKey;\n    LONG result = RegOpenKeyEx(hKeyRoot, subKey, 0, KEY_READ, &hKey);\n    if (result != ERROR_SUCCESS) {\n        return false; // Key does not exist or cannot be opened\n    }\n\n    result = RegQueryValueEx(hKey, valueName, nullptr, nullptr, data, &dataSize);\n    RegCloseKey(hKey);\n\n    return (result == ERROR_SUCCESS);\n}\n\nint main() {\n    const char* subKey = "blah\\blah\\blah";\n    const char* valueName = "foo";\n    BYTE data[256];\n    DWORD dataSize = sizeof(data);\n\n    if (ReadRegistryValue(HKEY_LOCAL_MACHINE, subKey, valueName, data, dataSize)) {\n        std::cout << "Value: " << data << std::endl;\n    } else {\n        std::cout << "Failed to read registry value or key does not exist." << std::endl;\n    }\n\n    return 0;\n}