#include <iostream>\n#include <atomic>\n#include <thread>\n\nstd::atomic<int> counter(0);\n\nvoid increaseCounter(int n) {\n    for (int i = 0; i < n; ++i) {\n        ++counter; // atomic increment\n    }\n}\n\nint main() {\n    std::thread t1(increaseCounter, 1000);\n    std::thread t2(increaseCounter, 1000);\n\n    t1.join();\n    t2.join();\n\n    std::cout << "Final counter value: " << counter << std::endl;\n\n    return 0;\n}