#include <windows.h>\n#include <iostream>\n\nvoid InitializeCOM()\n{\n    HRESULT hr = CoInitializeEx(NULL, COINIT_APARTMENTTHREADED);\n    if (FAILED(hr))\n    {\n        std::cerr << "Failed to initialize COM library. Error code: " << hr << std::endl;\n        // Handle the error appropriately\n    }\n}\n\nint main()\n{\n    InitializeCOM();\n\n    // Your code here\n\n    CoUninitialize();\n    return 0;\n}