#include <iostream>\n#include <ctime>\n#include <string>\n\nint main() {\n    time_t seconds;\n    time(&seconds);\n\n    // Convert time_t to a string\n    std::string timeStr = ctime(&seconds);\n\n    // Remove the newline character\n    if (!timeStr.empty() && timeStr[timeStr.length() - 1] == '\n') {\n        timeStr.erase(timeStr.length() - 1);\n    }\n\n    // Print the string\n    std::cout << timeStr << std::endl;\n\n    return 0;\n}