#include <iostream>\n\n#ifdef _WIN32\n    #include <windows.h>\n#else\n    #include <unistd.h>\n#endif\n\nvoid clearConsole() {\n    #ifdef _WIN32\n        // Clear console on Windows\n        system("cls");\n    #else\n        // Clear console on Linux/Unix\n        system("clear");\n    #endif\n}\n\nint main() {\n    std::cout << "Clearing the console..." << std::endl;\n    clearConsole();\n    return 0;\n}