#include <vector>\n#include <algorithm> // std::remove_if\n\nclass MyClass {\npublic:\n    bool isTiredOfLife() const {\n        // Implementation of the method\n    }\n};\n\nvoid removeTiredElements(std::vector<MyClass>& vec) {\n    vec.erase(\n        std::remove_if(vec.begin(), vec.end(), [](const MyClass& obj) {\n            return obj.isTiredOfLife();\n        }),\n        vec.end()\n    );\n}