#include <windows.h>\n#include <iostream>\n\ntypedef void (*FunctionType)();\n\nint main() {\n    HMODULE hModule = LoadLibrary("example.dll");\n    if (hModule != NULL) {\n        FunctionType func = (FunctionType)GetProcAddress(hModule, "ExportedFunction");\n        if (func != NULL) {\n            func();\n        } else {\n            std::cerr << "Could not locate the function" << std::endl;\n        }\n        FreeLibrary(hModule);\n    } else {\n        std::cerr << "Could not load the DLL" << std::endl;\n    }\n    return 0;\n}