#include <iostream>\n#include <cmath>\n#include <tuple>\n\nstd::tuple<double, double> calculatePoint(double radius, double angle, std::tuple<double, double> origin = {0, 0}) {\n    double x = std::get<0>(origin) + radius * cos(angle);\n    double y = std::get<1>(origin) + radius * sin(angle);\n    return std::make_tuple(x, y);\n}\n\nint main() {\n    auto [x, y] = calculatePoint(5, M_PI / 4);\n    std::cout << "x: " << x << ", y: " << y << std::endl;\n    return 0;\n}