// ConfigReader.h\n#pragma once\nusing namespace System;\nusing namespace System::IO;\n\npublic ref class ConfigReader\n{\npublic:\n    static String^ GetEngineName()\n    {\n        // A simple example of reading from a config file\n        String^ configFilePath = "config.txt";\n        if (File::Exists(configFilePath))\n        {\n            array<String^>^ lines = File::ReadAllLines(configFilePath);\n            for each (String^ line in lines)\n            {\n                if (line->StartsWith("[ENGINE]"))\n                {\n                    return line->Substring(8)->Trim();\n                }\n            }\n        }\n        throw gcnew InvalidOperationException("Engine configuration not found.");\n    }\n};