// main.cpp\n#include "ConfigReader.h"\n#include "EngineLoader.cpp"\n\nint main(array<System::String ^> ^args)\n{\n    try\n    {\n        String^ engineName = ConfigReader::GetEngineName();\n        IEngine^ engine = EngineLoader::LoadEngine(engineName);\n        engine->ProcessData();\n    }\n    catch (Exception^ ex)\n    {\n        Console::WriteLine("Error: {0}", ex->Message);\n    }\n\n    return 0;\n}