#include <iostream>\n#include <vector>\n#include <numeric>\n#include <algorithm>\n\nstd::vector<bool> patternRecognitionAlgorithm(const std::vector<double>& data) {\n    double threshold = std::accumulate(data.begin(), data.end(), 0.0) / data.size();\n    std::vector<bool> result(data.size());\n    std::transform(data.begin(), data.end(), result.begin(), [threshold](double d) { return d > threshold; });\n    return result;\n}\n\nint main() {\n    std::vector<double> data = {0.1, 0.4, 0.6, 0.8, 0.3};\n    std::vector<bool> result = patternRecognitionAlgorithm(data);\n    \n    for (bool val : result) {\n        std::cout << val << " ";\n    }\n    return 0;\n}