// Example steps to use Visual Studio Diagnostic Tools for memory usage\n#include <iostream>\n#include <vector>\n\nint main() {\n    std::vector<int> vec;\n    for (int i = 0; i < 1000000; ++i) {\n        vec.push_back(i);\n    }\n\n    // Take a snapshot here using Visual Studio Diagnostic Tools\n    std::cout << "Memory allocation done!" << std::endl;\n\n    // Perform more operations...\n\n    // Take another snapshot here and compare with the previous one\n    std::cout << "Further operations done!" << std::endl;\n\n    return 0;\n}