#include <windows.h>\n#include <iostream>\n\ntypedef int (__stdcall *FooFunction)(long);\n\nint main() {\n    HMODULE hModule = LoadLibrary("yourdll.dll");\n    if (hModule) {\n        FooFunction foo = (FooFunction)GetProcAddress(hModule, "foo");\n        if (foo) {\n            int result = foo(42);\n            std::cout << "Result: " << result << std::endl;\n        } else {\n            std::cerr << "Failed to get function address" << std::endl;\n        }\n        FreeLibrary(hModule);\n    } else {\n        std::cerr << "Failed to load DLL" << std::endl;\n    }\n    return 0;\n}