#include <windows.h>\n#include <tchar.h>\n#include <strsafe.h>\n\nvoid GetApplicationName()\n{\n    TCHAR szFileName[MAX_PATH];\n    if (GetModuleFileName(NULL, szFileName, MAX_PATH))\n    {\n        // szFileName now contains the full path to the executable\n        // Extract just the file name from the path\n        TCHAR* pFileName = _tcsrchr(szFileName, '\\');\n        if (pFileName)\n        {\n            pFileName++; // Move past the backslash\n            _tprintf(_T("Application Name: %s\n"), pFileName);\n        }\n    }\n    else\n    {\n        // Handle error\n        _tprintf(_T("Failed to get module file name. Error: %lu\n"), GetLastError());\n    }\n}