#include <iostream>\n#include <openssl/aes.h>\n#include <openssl/rand.h>\n\n// Function to encrypt data\nvoid encryptData(const unsigned char* plaintext, int plaintext_len, unsigned char* key, unsigned char* ciphertext) {\n    AES_KEY enc_key;\n    AES_set_encrypt_key(key, 256, &enc_key);\n    AES_encrypt(plaintext, ciphertext, &enc_key);\n}\n\n// Function to decrypt data\nvoid decryptData(const unsigned char* ciphertext, unsigned char* key, unsigned char* plaintext) {\n    AES_KEY dec_key;\n    AES_set_decrypt_key(key, 256, &dec_key);\n    AES_decrypt(ciphertext, plaintext, &dec_key);\n}\n\nint main() {\n    unsigned char key[32];\n    RAND_bytes(key, sizeof(key)); // Generate a random key\n\n    unsigned char plaintext[] = "Sensitive Data";\n    unsigned char ciphertext[128];\n    unsigned char decryptedtext[128];\n\n    // Encrypt the data\n    encryptData(plaintext, strlen((char*)plaintext), key, ciphertext);\n\n    // Decrypt the data\n    decryptData(ciphertext, key, decryptedtext);\n\n    // Ensure the decrypted text is null-terminated\n    decryptedtext[strlen((char*)plaintext)] = '\0';\n\n    std::cout << "Decrypted text: " << decryptedtext << std::endl;\n\n    return 0;\n}