#include <windows.h>\n#include <sql.h>\n#include <sqlext.h>\n#include <string>\n\nvoid bindAndSendTextData(SQLHSTMT hStmt, const std::string& textData) {\n    SQLRETURN retcode;\n    SQLLEN lengthIndicator = SQL_LEN_DATA_AT_EXEC(textData.size());\n\n    // Bind parameter\n    retcode = SQLBindParameter(\n        hStmt,\n        1,                          // Parameter number\n        SQL_PARAM_INPUT,            // Input parameter\n        SQL_C_CHAR,                 // C data type\n        SQL_LONGVARCHAR,            // SQL data type\n        textData.size(),            // Column size\n        0,                          // Decimal digits: not applicable\n        (SQLPOINTER)1,              // Placeholder for the data\n        0,                          // Buffer length: not applicable\n        &lengthIndicator            // Buffer to store actual data length\n    );\n\n    if (retcode != SQL_SUCCESS && retcode != SQL_SUCCESS_WITH_INFO) {\n        // Handle error\n        return;\n    }\n\n    // Execute the statement\n    retcode = SQLExecute(hStmt);\n    if (retcode == SQL_NEED_DATA) {\n        SQLPOINTER token;\n        while ((retcode = SQLParamData(hStmt, &token)) == SQL_NEED_DATA) {\n            // Send the actual data\n            retcode = SQLPutData(hStmt, (SQLPOINTER)textData.c_str(), textData.size());\n            if (retcode != SQL_SUCCESS && retcode != SQL_SUCCESS_WITH_INFO) {\n                // Handle error\n                return;\n            }\n        }\n    }\n\n    if (retcode != SQL_SUCCESS && retcode != SQL_SUCCESS_WITH_INFO) {\n        // Handle error\n    }\n}