#include <windows.h>\n#include <iostream>\n\nbool IsModuleHandleValid(HMODULE hModule, const char* moduleName) {\n    HMODULE hModuleCheck = GetModuleHandle(moduleName);\n    return (hModuleCheck == hModule);\n}\n\nint main() {\n    // Example usage\n    HMODULE hModule = GetModuleHandle("kernel32.dll");\n    if (hModule) {\n        std::cout << "Kernel32.dll module handle is valid.\n";\n    } else {\n        std::cout << "Kernel32.dll module handle is not valid.\n";\n    }\n\n    // Check if the handle is still valid\n    if (IsModuleHandleValid(hModule, "kernel32.dll")) {\n        std::cout << "The module handle is still valid.\n";\n    } else {\n        std::cout << "The module handle is no longer valid.\n";\n    }\n\n    return 0;\n}