#include <iostream>\n#include <optional>\n#include <string>\n\nvoid foo(const std::optional<std::string>& input) {\n    if (input) {\n        std::cout << input->size() << std::endl;\n    } else {\n        std::cout << "NULL input" << std::endl;\n    }\n}\n\nvoid bar() {\n    foo("stackoverflow");\n    foo(std::nullopt); // Passing no string\n}\n\nint main() {\n    bar();\n    return 0;\n}