#include <iostream>\n#include <vector>\n#include <algorithm>\n\nint main() {\n    std::vector<int> data = {1, 2, 3, 4, 6, 7, 8, 9};\n\n    int target = 5;\n    auto it = std::lower_bound(data.begin(), data.end(), target);\n\n    if (it != data.end() && *it == target) {\n        std::cout << "Element found: " << *it << std::endl;\n    } else {\n        std::cout << "Element not found" << std::endl;\n    }\n\n    return 0;\n}